(function () {
  const VN = /[\u0300-\u036f]/g;
  const norm = s => (s||"").toLowerCase().normalize("NFD").replace(VN,"").replace(/\s+/g," ").trim();
  const tokenize = s => norm(s).split(/[,\.;\/\-\|\n\s]+/).filter(Boolean);
  function score(plain, set, terms){
    if(!terms) return 0; let s=0;
    for(const raw of terms){
      const t = norm(raw); if(!t) continue;
      if(plain.includes(t)){ s+=Math.max(3, Math.floor(t.length/6)); continue; }
      const parts = t.split(/\s+/).filter(Boolean);
      let k=0, ok=true;
      for(const p of parts){ if(set.has(p)) k++; else{ ok=false; break;}}
      if(ok) s+=Math.max(1, Math.floor(k/2));
    }
    return s;
  }
  function diagnoseFromKeywords(q, topN=6){
    const data = window.DIAGNOSE_DATA||[];
    const tokens = tokenize(q||""); if(!tokens.length) return [];
    const plain = tokens.join(" "); const set = new Set(tokens);
    return data.map(d=>({label:d.label, icon:d.icon||"", score:score(plain,set,d.terms)}))
      .filter(x=>x.score>0).sort((a,b)=>b.score-a.score).slice(0,topN);
  }
  function suggestKeywords(query){
  const q = (query || "").toLowerCase().trim();
  if(!q) return [];
  const out = new Set();

  (window.DIAGNOSE_DATA || []).forEach(d => {
    (d.terms || []).forEach(t => {
      if(t.toLowerCase().includes(q)) out.add(t);
    });
  });

  return Array.from(out).slice(0, 12); // giá»›i háº¡n hiá»ƒn thá»‹ 12 tá»«
}

  window.diagnoseFromKeywords = diagnoseFromKeywords;
})();
